import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from '../../../../node_modules/axios';
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
import StarRatings from 'react-star-ratings';



class ItemReseñaAdmin extends Component {
    constructor(props) {
        super(props)
        this.state = {
            reseña:props.reseña,
         
           
        }
      

    }

   
    
    async componentDidMount(){
       
    }

    eliminarReseña=()=>{

    }

    marcarComoSpoiler=()=>{

    }

    render() {
      
       

        return (
            <div>
                <div class="card-body" >
                </div>
                <div class="card col-md-6 offset-md-3 text-light bg-dark" >
                    <div class="card-body">

                        <h5 className="card-title "> {this.props.reseña.titulo} </h5>
                        <StarRatings
                        rating={this.state.ranting}
                        starRatedColor={'yellow'}
                        
                        numberOfStars={5}
                        name='rating'
                        starDimension="30px"
                        starSpacing="15px"
                        starHoverColor="yellow"
                        starSelectingHoverColor="yellow"
                        isSelectable={true}
                        isAggregateRating={true}

                    />
               
                        <button className="btn btn-outline-danger itemBoton" onClick={() => confirmAlert({
                            customUI: ({ onClose }) => {
                                return (
                                    <div className='custom-ui'>
                                        <h1>¿Está seguro?</h1>
                                        {' '}
                                        
                                        <button onClick={onClose}>No</button> {' '}
                                        <button
                                            onClick={() => {
                                                this.eliminarReseña();
                                                onClose();


                                            }}
                                        >
                                            Si, deseo eliminar
                            </button>
                                    </div>
                                );
                            }
                        })}>Eliminar</button> {''}
                        <button className="btn btn-outline-danger itemBoton" onClick={this.marcarComoSpoiler}> Marcar como spolier </button>
 
                    </div>
                </div>
            </div>
        )
    }
}

export default ItemReseñaAdmin;
