import React, { Component } from "react";
import axios from "axios";

//Constante a la cual hacemos la consulta
const librosApi = "http://localhost:4000/api/libros/me";
const capitulosApi = "http://localhost:4000/api/libros/verCapitulo";
const capitulosMuchosApi = "http://localhost:4000/api/libros/capitulosMuchos";
const existeL = "http://localhost:4000/api/libros/existeLibro";
const existeC = "http://localhost:4000/api/libros/existeCapitulo";
const miperfilApi = "http://localhost:4000/api/perfiles/me";

export default class MiSuscripcion extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: sessionStorage.getItem("token"),
      miPerfil: [],
      historialLibros: [],
      historialCapitulos: [],

      librosConInfoTerminado: [],
      capitulosConInfoTerminado: [],

      libros: [],
      capitulos: [],
    };
    this.filtrar = this.filtrar.bind(this);
    this.getLibro = this.getLibro.bind(this);
    this.getCapitulo = this.getCapitulo.bind(this);
    this.renderLibros = this.renderLibros.bind(this);
  }

  renderLibros = () => {
    return (
      <div>
        {this.state.libros.map((hola) => {
          console.log(hola);
        })}
      </div>
    );
  };
  getLibro = async (libro) => {
    await axios
      .post(
        librosApi,
        { id: libro.libro },
        { headers: { xaccess: sessionStorage.getItem("token") } }
      )
      .then((res) => {
        this.state.libros.push(res.data);
      });
  };

  getCapitulo = async (capitulo) => {
    await axios
      .post(
        capitulosApi,
        { id: capitulo.capitulo },
        { headers: { xaccess: sessionStorage.getItem("token") } }
      )
      .then((res) => {
        this.state.capitulos.push(res.data);
      });
  };

  filtrar = async () => {
    //filtro los existentes

    if (this.state.historialLibros.length > 0) {
      var existentes = new Array();
      this.state.historialLibros.map(async (libro) => {
        await axios
          .post(
            existeL,
            {
              id: libro._id,
            },
            { headers: { xaccess: sessionStorage.getItem("token") } }
          )
          .then((res) => {
            if (res.data.existe) {
              existentes.push(libro);
            }
          });
      });
      this.setState({
        historialLibros: existentes,
        librosConInfoTermiado: existentes,
      });
      if (this.state.historialLibros.length) {
        this.getLibros();
      }
    }

    if (this.state.historialCapitulos.length > 0) {
      var existentes = new Array();
      this.state.historialCapitulos.map(async (capitulo) => {
        await axios
          .post(
            existeC,
            {
              id: capitulo._id,
            },
            { headers: { xaccess: sessionStorage.getItem("token") } }
          )
          .then((res) => {
            if (res.data.existe) {
              existentes.push(capitulo);
            }
          });
      });

      this.setState({
        historialCapitulos: existentes,
        capitulosConInfoTerminado: existentes,
      });

      if (this.state.historialCapitulos) {
        this.render();
        //this.getCapitulos();
      }
    }
  };
  async componentDidMount() {
    var historialL = new Array();
    var historialC = new Array();

    await axios
      .get(miperfilApi, {
        headers: { xaccess: sessionStorage.getItem("token") },
      })
      .then((res) => {
        res.data.historialLibros.map((libro) => {
          historialL.push(libro);
        });
        res.data.historialCapitulos.map((cap) => {
          historialC.push(cap);
        });
        this.setState({
          historialLibros: historialL,
          historialCapitulos: historialC,
          miPerfil: res.data,
        });
        this.filtrar();
      })
      .catch((err) => {
        alert(err.response);
      });
  }

  render() {
    this.state.historialLibros.map((libro) => {
      this.getLibro(libro);
    });

    this.state.historialCapitulos.map((capitulo) => {
      this.getCapitulo(capitulo);
    });
    var librosParaMapear = this.state.libros;
    var capitulosParaMapear = this.state.capitulos;

    return (
      <div className="container">
        <div className="cardVS col-md-6 offset-md-3 text-light bg-dark">
          <div>
            <h2 className="card-header">
              {" "}
              Historial de {this.state.miPerfil.nombre}{" "}
            </h2>
          </div>

          <div className="card-body">
            {librosParaMapear ? (
              (console.log("hay libros para mapear"),
              //librosParaMapear.map((libro, index) => (
              //<div key={index}> {libro._id}</div>
              // )))
              this.renderLibros())
            ) : (
              <div>hola</div>
            )}
            {capitulosParaMapear ? (
              (console.log("hay capitulos para mapear"),
              capitulosParaMapear.map((capitulo, index) => (
                <div key={index}> {capitulo._id} </div>
              )))
            ) : (
              <div> hola</div>
            )}
          </div>
        </div>
      </div>
    );
  }
}
