import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import 'react-confirm-alert/src/react-confirm-alert.css';
import StarRatings from 'react-star-ratings';
import axios from "axios";

const me = "http://localhost:4000/api/libros/me";

class Reseña extends Component {
    constructor(props) {
        super(props)
        this.state = {
            idLibro: this.props.match.params.id,
            calificacion: null,
            titulo: '',
            cuerpo: '',
            ranting: 0,
            libro:[],
        }

        this.changeRating = this.changeRating.bind(this);
        this.onInputChange = this.onInputChange.bind(this);


    }


    getDatos = async () => {
        await axios
          .post(
            me,
            { id: this.state.idLibro },
            { headers: { xaccess: this.state.token } }
          )
          .then((res) => {
            console.log(res);
            this.setState({
              libro: res.data
            });
          })
          .catch((err) => {
            console.log(err);
          });
      };

    changeRating = (newRating, name) => {
        this.setState({ ranting: newRating })
    }

    componentDidMount = async () => {
        this.getDatos();

    }

    onSubmit = (e) => {
        e.preventDefault();
        console.log("Usted ha enviado la siguiente información: ", 
            "Calificación del libro: ", this.state.ranting,
            "Titulo: ", this.state.titulo,
            "Cuerpo: ", this.state.cuerpo);

    }

    onInputChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    };


    render() {


        return (
            <div className="container-reseña">
                <div className="cardReseña col-md-6 offset-md-3 text-light bg-dark ">
                    <h4 className="card-header">Reseña para el libro: {this.state.libro.titulo} </h4>

                    <span><h5 className="card-header">Calificación </h5></span>

                    <StarRatings
                        rating={this.state.ranting}
                        starRatedColor={'yellow'}
                        changeRating={this.changeRating}
                        numberOfStars={5}
                        name='rating'
                        starDimension="30px"
                        starSpacing="15px"
                        starHoverColor="yellow"
                        starSelectingHoverColor="yellow"
                        isSelectable={true}
                        isAggregateRating={true}

                    />




                    <br></br>
                    <br></br>



                    <form onSubmit={this.onSubmit} >

                        <div className="form-group">
                            <input
                                className="form-control"
                                id="titulo"
                                name="titulo"
                                onChange={this.onInputChange}
                                value={this.state.titulo}
                                placeholder="Título"
                                required>
                            </input>
                        </div>

                        <div className="form-group">
                            <textarea className="form-control"
                                id="cuerpo"
                                rows="3"
                                name="cuerpo"
                                onChange={this.onInputChange}
                                value={this.state.cuerpo}
                                placeholder="Descripción"
                                required >
                            </textarea>
                        </div>

                        <div className="form-group">
                            <button type="submit" className="btn  btn-outline-danger itemBoton float-right">
                                Agregar reseña
                            </button>
                        </div>


                    </form>






                </div>
            </div>

        )
    }
}

export default Reseña;
