import React, { Component } from 'react';
import axios from 'axios';
import ItemPerfil from './ItemPerfil';
import { Link } from 'react-router-dom';


//Pregunto que suscriptor soy
const yoApi = 'http://localhost:4000/api/suscriptores/me'
//Poner constante a la cual le hacemos la consulta de nuestros pefiles como suscriptor
const perfilesApi = 'http://localhost:4000/api/suscriptores/loginPerfiles'

class ListarPerfiles extends Component{
    constructor(){
        super();
        this.state={
            user: JSON.parse(sessionStorage.getItem('user')),
            token: sessionStorage.getItem('token'),
            miSuscripcion:[],
            perfiles: []

        }
    }


    setSusripcion(res){
        console.log(res);
        this.setState({
            miSuscripcion:res
        });
    }

    getYo = async () =>{
        await axios.get(yoApi,{
            headers:{'xaccess':this.state.token}  
        })
        .then(res =>{
            this.setSusripcion(res.data)
        })
        .catch(err =>{
            alert(err.response)});

    }
    //Seteamos los perfiles en el arrgelo
    setPerfiles (res) {
        this.setState({perfiles:res});
    }

    //Hacemos la consulta de los perfiles del suscriptor
    getPerfiles = async () => {
        await axios.post( perfilesApi,
             this.state.miSuscripcion._id,
             {headers: {'xaccess': sessionStorage.getItem('token')}}

        ) 
        .then (res => {
            console.log(res);
            this.setPerfiles(res.data)
        })
        .catch();
    }

    async componentDidMount(){
        this.getYo();
        this.getPerfiles();

    }

    render (){
        return(
            <div className="container">
                {this.state.perfiles.map(per =>
                    <div>
                      {per.map( perfil =>
                        <div>
                            <ItemPerfil perfil={perfil}></ItemPerfil>
                        </div>)}  
                    </div>
                    )}

            </div>
        )
    }

}



export default ListarPerfiles;
