import React, { Component } from 'react';
import axios from 'axios';
import '../../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import DatePicker from '../../../../node_modules/react-datepicker';
import '../../../../node_modules/react-datepicker/dist/react-datepicker.css';
import ItemListUsuario from './ItemListUsuario';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";


const getSuscriptores = 'http://localhost:4000/api/suscriptores';

class ReporteDeUsuarios extends Component {
    constructor(props){
        super(props);
        this.state={
            desde:'',
            hasta:'',
            token: sessionStorage.getItem('token'),
            suscriptores: [],
            usuariosCarrusel:[],
        }

    }


    
    async componentDidMount(){

        await axios.get(getSuscriptores, {
            headers: { 'xaccess': this.state.token }
        })
        .then(res => {
            this.setState({
                suscriptores: res.data
            });
        })
        .catch(err => {
            console.log(err)
        });

    }

    setUsuariosCarrusel(res){
        
       
        var num =2;
        var aux=[]; 
        var aux2=[];

        res.map(no=>{
            if(aux.length< num){
                aux.push(no);
            }else{
                aux.push(no);
                aux2.push(aux);
                aux=[] 
            };
        })
        if(aux!=[]){
            aux2.push(aux);
        }

        this.setState({
            
            usuariosCarrusel:aux2
        });



    }

    onChangeFecha = desde => {
        this.setState({ desde });
    };
    onChangeFechaHasta = hasta => {
        this.setState({ hasta });
    };

    onSubmit=(e)=>{
        e.preventDefault();
        console.log(this.state.desde);
        console.log(this.state.hasta);
        this.setUsuariosCarrusel(this.state.suscriptores);
    }

    limpiar=()=>{
       
     
        this.setState({

            usuariosCarrusel:[],

        });
    
    }

    render() {
        const settings = {
            dots: true,
            infinite: false,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            className: 'slides',
            
          };
        return (
            
            <div>

                <h1 className="col-md-8 offset-md-4  text-light "> {'Buscar Usuarios' } </h1>
                <br></br>

                <div className="offset-md-3  text-light ">
                <form  onSubmit={this.onSubmit} autoComplete='off'>
                    
                    
         
                 
            <text >{' Desde '}</text>
            <DatePicker className="form-control"
                    selected={this.state.desde}
                    name='desde'
                    onChange={this.onChangeFecha}
                     
            />
            <text>{' Hasta '}</text>
            <DatePicker className="form-control"
                    selected={this.state.hasta}
                    name='hasta'
                    onChange={this.onChangeFechaHasta}
                     
            />
          

                    


                    <button type ="submit" className="btn btn-light itemBoton  "> 
                        <svg class="bi bi-search" width="1em" height="1em" viewBox="0 0 16 16" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                        <path fill-rule="evenodd" d="M10.442 10.442a1 1 0 0 1 1.415 0l3.85 3.85a1 1 0 0 1-1.414 1.415l-3.85-3.85a1 1 0 0 1 0-1.415z"/>
                        <path fill-rule="evenodd" d="M6.5 12a5.5 5.5 0 1 0 0-11 5.5 5.5 0 0 0 0 11zM13 6.5a6.5 6.5 0 1 1-13 0 6.5 6.5 0 0 1 13 0z"/>
                        </svg>
                    </button>    
                    <button onClick={this.limpiar()} className="btn btn-light itemBoton "> 
                    
                        <svg class="bi bi-backspace" width="1em" height="1em" viewBox="0 0 16 16" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                        <path fill-rule="evenodd" d="M6.603 2h7.08a1 1 0 0 1 1 1v10a1 1 0 0 1-1 1h-7.08a1 1 0 0 1-.76-.35L1 8l4.844-5.65A1 1 0 0 1 6.603 2zm7.08-1a2 2 0 0 1 2 2v10a2 2 0 0 1-2 2h-7.08a2 2 0 0 1-1.519-.698L.241 8.65a1 1 0 0 1 0-1.302L5.084 1.7A2 2 0 0 1 6.603 1h7.08z"/>
                        <path fill-rule="evenodd" d="M5.83 5.146a.5.5 0 0 0 0 .708l5 5a.5.5 0 0 0 .707-.708l-5-5a.5.5 0 0 0-.708 0z"/>
                        <path fill-rule="evenodd" d="M11.537 5.146a.5.5 0 0 1 0 .708l-5 5a.5.5 0 0 1-.708-.708l5-5a.5.5 0 0 1 .707 0z"/>
                        </svg>
                    
                     </button>         

                </form>

                </div>

                <div className="carrusel">
                        <Slider {...settings} >
        
                            {this.state.usuariosCarrusel.map(usuarios => 
                            <div>
                               {usuarios.map( usuario=>
                                <div>
                                    <ItemListUsuario usuario={usuario}></ItemListUsuario>      
                                </div>  
                                )}
                            </div>
                            )}                          
                        </Slider>
                        </div>
                
            </div>
        )
    }
}
export default ReporteDeUsuarios;
