import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import 'react-confirm-alert/src/react-confirm-alert.css';



class ItemNovedad extends Component {
    constructor(props) {
        super(props)
        this.state = {
            capitulo: [],

        }



    }



    render() {
        this.state.capitulo = this.props.libro.capitulos;
        console.log(this.state.capitulo)
        return (
            (this.state.capitulo == null) ?
                <div>
                    <div class="card-body" >
                    </div>
                    <div class="card col-md-6 offset-md-3 text-light bg-dark" >
                        <div class="card-body">


                            <h5 className="card-title ">{this.props.libro.titulo} </h5>

                            <Link className='btn btn-outline-success itemBoton' to={'/suscriptor/libros/' + this.props.libro._id}  >
                                Ver detalle
                           </Link>

                            <Link className='btn btn-outline-success itemBoton' to={'/suscriptor/libros/leer/' + this.props.libro.archivo}  >
                                Leer Libro
                           </Link>




                        </div>
                    </div>
                </div>

                :
                <div>
                    <div class="card-body" >
                    </div>
                    <div class="card col-md-6 offset-md-3 text-light bg-dark" >
                        <div class="card-body">


                            <h5 className="card-title ">{this.props.libro.titulo} </h5>

                            <Link className='btn btn-outline-success itemBoton' to={'/suscriptor/libros/' + this.props.libro._id}  >
                                Ver detalle
                   </Link>

                            <Link className='btn btn-outline-success itemBoton' to={'/suscriptor/libros/leer/' + this.props.libro.archivo}  >
                                Leer Capitulo
                   </Link>




                        </div>
                    </div>
                </div>
        )
    }
}

export default ItemNovedad;
