import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';



const miperfilApi = 'http://localhost:4000/api/perfiles/me';

class ListadoPerfiles extends Component {
    constructor() {
        super();
        this.state = {
            token: sessionStorage.getItem("token"),
            suscripcion: JSON.parse(sessionStorage.getItem("user")),
            Perfil: JSON.parse(sessionStorage.getItem("perfilUser")),
            miPerfil: []
        }
    }




    getData = async () => {
        await axios.get(miperfilApi, {
            headers: { 'xaccess': sessionStorage.getItem('token') }
        })
            .then(res => {
                this.setState({
                    miPerfil: res
                });
            })
            .catch(err => {
                alert(err.response)
            });

    }


    componentDidMount = async () => {
        this.getData();
        console.log("Ac치 presta atenci칩n", this.state.suscripcion.user.perfiles)
    }




    render() {
        return (
            <div>

                {this.state.suscripcion.user.perfiles.map(perfil =>
                    <div className="cardListarPerfil">

                        <h5> {perfil.nombre} </h5> <button className="btn btn-outline-danger itemBoton float-right">Eliminar</button>
                        <button className="btn btn-outline-danger itemBoton float-right">Ingresar</button>

                    </div>

                )}

            </div>
        )

    }
}

export default ListadoPerfiles;
