import React, { Component } from '../../../../node_modules/react'
import '../../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import DatePicker from '../../../../node_modules/react-datepicker';
import '../../../../node_modules/react-datepicker/dist/react-datepicker.css';
import axios from '../../../../node_modules/axios';

const modificar = 'http://localhost:4000/api/trailers/modificar';
const me ='http://localhost:4000/api/trailers/me';
const portada = 'http://localhost:4000/uploads/';
const libros = 'http://localhost:4000/api/libros';

class ModificarTrailer extends Component {

    constructor(props){
        super(props);
            this.state = {
                token: sessionStorage.getItem('token'),
                id: this.props.match.params.id,
                titulo: '',
                descripcion: '',
                libro: '',
                hola: '',
                video: '',
                libros: []
    };
        this.getPortada=this.getPortada.bind(this);
    }



    onSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('titulo', this.state.titulo);
        formData.append('descripcion', this.state.descripcion);
        if (this.state.libroseleccionado != null){
            formData.append('libro', this.state.libroseleccionado);}
        if (this.state.archivo != null){
            formData.append('hola', this.state.archivo);
        }
        if (this.state.video != null){
            formData.append('video', this.state.video);
        }
        for (var value of formData.values()) {
            console.log(value); }
        axios.post(modificar,formData,{
                headers: { 'xaccess':this.state.token }
            })
            .then(res => {
                alert("Trailer cargado con exito");
                return (window.location = '/trailers')
            })
            .catch(err => {
                alert(JSON.stringify(err.response.data.msg))
            } );

    };

    onInputChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    };


    getPortada(e){

        this.setState({
            portadaImg: e.target.files[0]
        })

    }
    setTrailer=(trailer)=>{

        this.setState({

            titulo: trailer.titulo,
            descripcion:  trailer.descripcion,
            libro: trailer.libro,
            hola: trailer.hola,
            video: trailer.video

        })

    };

    getPdf = (e) => {
        if (this.state.archivo != null)
        { return alert ("Ya se ha cargado un video, no se pueden cargar ammbos")}
        else {
        this.state.video="no";
        this.state.archivo= e.target.files[0];}
    console.log(this.state.video, this.state.archivo)
}


getVideo = (e) => {
    if (this.state.archivo != null)
    { return alert ("Ya se ha cargado un pdf, no se pueden cargar ammbos")}
    else {
        this.state.video='si';
        this.state.archivo= e.target.files[0];
       }


}

getLibros = async () => {
        const {user} = this.state.user;
        await axios.get(libros,{
            user: user,
            headers:{'xaccess':this.state.token}  
        })
        .then(res =>{
                this.setState({ libros :res.data
                })
        })
        .catch();



    }


    getData = async () => {


        await axios.post(me,
          { id: this.state.id },
          { headers:{'xaccess': this.state.token}}
        ).then(res =>{
            console.log("Lo que devuelve me",res.data)
            this.setTrailer(res.data);
        })
        .catch(err =>{console.log(err)})

    }

    async componentDidMount(){
        this.getData();
        this.getLibros();
    }


    render(){
        return (
        <div className="form-novedad" >

        <div className="col-md-6 offset-md-3">
        <div className="card card-body text-light bg-dark">

        <form onSubmit={this.onSubmit} >


        <h2 className="card-header"> Modifcar Novedad</h2>
            <div className="form-group">
                <h5>Titulo</h5>
                <input 
                    className="form-control" 
                    id="exampleFormControlInput1" 
                    name ="titulo"
                    onChange={this.onInputChange}
                    value={this.state.titulo}
                    placeholder="Título"
                    required>
                </input>
            </div>

            <div className="form-group">
            <h5>Descripción</h5>
                <textarea className="form-control" 
                    id="exampleFormControlTextarea1" 
                    rows="3"
                    name ="descripcion"
                    onChange={this.onInputChange}
                    value={this.state.descripcion}
                    placeholder="Descripción"
                    required >
                </textarea> 
            </div>

            <select className="form-control"  onChange={this.onInputChangeLibro} id="exampleFormControlSelect1" name="libros" >
                <option value="this.state.libro" >Elija un libro asociado (Opcional)</option>
                {this.state.libros.map(libro =>

                <option key={libro._id} value={libro._id} >{libro.titulo}</option>
                )}
            </select>
            <br></br>
            <div className="form-group">
                <button type ="submit" className="btn btn-success float-right">
                    Modificar Trailer       
                </button>
            </div>


         </form>
         </div>
         </div>   
         </div>
        )
    }
}
export default  ModificarTrailer;
