import React, { Component } from 'react';
import { Link ,Redirect} from 'react-router-dom';
import axios from 'axios';


const loginPerfilApi = "http://localhost:4000/api/suscriptores/loginPerfil";
const miperfilApi = 'http://localhost:4000/api/perfiles/me';

class ListadoPerfiles extends Component {
    constructor() {
        super();
        this.state = {
            token: sessionStorage.getItem("token"),
            suscripcion: JSON.parse(sessionStorage.getItem("user")),
            Perfil: JSON.parse(sessionStorage.getItem("perfilUser")),
            miPerfil: []
        }
    }


    ingresarPerfil = async (e, idllega) => {
        console.log("id que llega " ,idllega)
        await axios
      .post(
        loginPerfilApi,
        { id:idllega },
        {
          headers: { xaccess: sessionStorage.getItem("token") },
        }
      )
      .then((res) => {
        console.log(res.data.user._id);

        const { user, token } = res.data;
        sessionStorage.setItem("token", token);
        sessionStorage.setItem("perfilUser", JSON.stringify(user));
        sessionStorage.setItem("perfil", res.data.user);
        sessionStorage.setItem("perfilID", user._id);
         return <Redirect to='/homesuscriptor'/>


        });

    }



    getData = async () => {
        await axios.get(miperfilApi, {
            headers: { 'xaccess': sessionStorage.getItem('token') }
        })
            .then(res => {
                this.setState({
                    miPerfil: res
                });
            })
            .catch(err => {
                alert(err.response)
            });

    }


    componentDidMount = async () => {
        this.getData();
        console.log("Ac치 presta atenci칩n", this.state.suscripcion.user.perfiles)
    }




    render() {
        return (
            <div>

                {this.state.suscripcion.user.perfiles.map(perfil =>
                    <div className="cardListarPerfil">

                        <h5> {perfil.nombre} </h5> <Link className="btn btn-outline-danger itemBoton float-right">Eliminar</Link>
                        <button onClick={(e)=> {this.ingresarPerfil(e, perfil._id)}} className="btn btn-outline-danger itemBoton float-right">Ingresar</button>

                    </div>

                )}

            </div>
        )

    }
}

export default ListadoPerfiles;
