import React, { Component } from 'react'
import { Document, Page } from 'react-pdf/dist/entry.webpack';

class LeerLibros extends Component {

    constructor(){
        super()
        this.state = {
          numPages: null,
          pageNumber: 1,
          terminado:false
        }
    }
        Termino = () => {
            if (this.state.pageNumber == this.state.numPages){
                this.state.terminado=true;
                alert("Ha terminado el libro satisfactoriamente");

            } else {
                alert("Aun no has terminado de leer")
            }
        }
        onDocumentLoad = ({ numPages }) => {   
          this.setState({ numPages });

        }

        render() {
          const { pageNumber, numPages } = this.state;

          return (
            <div className="container-leer">
                <p>Page {pageNumber} of {numPages}</p>
              <button onClick={(e) => {
                e.preventDefault(); 
               if (this.state.pageNumber>1){
                this.setState(prevState => ({ pageNumber: prevState.pageNumber - 1 }))}
                } }>
                Atras
                </button>
              <button onClick={(e) => { 
                e.preventDefault();
                if (this.state.pageNumber<this.state.numPages){
                    this.setState(prevState => ({ pageNumber: prevState.pageNumber + 1 }))}
                    else {
                        alert('El libro ha llegado a su fin')
                    }  }}>
                Siguiente>>
                </button>
                <button onClick={this.Termino}>
                    Terminé el libro sorete
                </button>
            <div className="lector">
              <Document
                file={'http://localhost:4000/uploads/Práctica 1.pdf'}

                onLoadSuccess={this.onDocumentLoad}
              >
                <Page pageNumber={pageNumber} />
              </Document>
              </div>
            </div>
          );
        }
      }


export default LeerLibros;

