import React, { Component } from 'react';
import axios from 'axios';
import NavegacionAdmin from '../NavegacionAdmin'

const generos = 'http://localhost:4000/api/generos/';
const cargar = 'http://localhost:4000/api/generos/cargar';
const borrar = 'http://localhost:4000/api/generos/eliminar/';
const modificar = 'http://localhost:4000/api/generos/modificar/';


class Generos extends Component {
    constructor() {
        super();
        this.state = {
            user: JSON.parse(sessionStorage.getItem('user')),
            token: sessionStorage.getItem('token'),
            
            generos: [],
            nombre: '',
            nombre2:'',
            id: ''
        };
        this.handleChange = this.handleChange.bind(this);
        this.agregarGenero = this.agregarGenero.bind(this);
        this.onInputChange = this.onInputChange.bind(this);
        

    }
    async getData() {
        await axios.get(generos, 
            { headers: { 'xaccess': this.state.token }
        })
            .then(res => {
                this.setState({
                    generos: res.data
                });
            })
            .catch(err => {
                alert(err)
            });

    }
    async componentDidMount() {
        await this.getData();
    };
    
    handleChange= (e) => {
        
        this.setState({
            [e.target.name]: e.target.value
        }

        );
    }
    handleChange2= (e) => {
        
        this.setState({
            [e.target.name]: e.target.value
        }

        );
    }
    

    onInputChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    };

    agregarGenero = async (e) => {
        e.preventDefault();
        if(generos.indexOf(this.state.nombre) > -1){
            return alert('El gÃ©nero ya fue cargado anteriormente')
        };
        await axios.post(cargar,
            { nombre: this.state.nombre },
            { headers: { 'xaccess': this.state.token } }

        ).then(res => {
            alert(JSON.stringify(res.data));
            
            this.getData()
        })

        .catch(err => {
            alert(JSON.stringify(err.data))
        } );

    };

    onInputChange2 = (e) => {
       this.setState({
            id: e.target.value
        });
    };

    eliminarGenero = async (e) => {
        e.preventDefault();

        await axios.post(borrar ,
            { id: this.state.id },
            { headers: { 'xaccess': this.state.token } }

        ).then(res => {
            this.getData();
            alert(JSON.stringify(res.data));
        })
        .catch(err => {
            alert(JSON.stringify(err.response.data.msg))
        });
    };

    onInputChange3 = (e) => {
        this.setState({
            id: e.target.value
        });
    };

    modificarGenero = async (e) => {
        e.preventDefault();

        await axios.post(modificar,
            { id: this.state.id,
            nombre: this.state.nombre2 },
            { headers: { 'xaccess': this.state.token } }

        ).then(res => {
            this.getData();
            alert(JSON.stringify(res.data));
        })
        .catch(err => {
            alert(JSON.stringify(err.data))
        });
    };


    render() {
        return (
            <div><NavegacionAdmin/>
            
            <div className="row">
                
                <div className="form-autor" >
                <div className="form-input-field">
                    <form onSubmit={this.agregarGenero} >


                    <div className="cardEditorial offset-md-1">
                    <div className="card-header">
				            <h5>Agregar gÃ©nero</h5>
                            </div>

                            <div className="card-body">   
                            <label className="text-light">Ingrese el gÃ©nero</label>
                                <input 
                                    className="form-control col s12"
                                    id="nombre"
                                    name="nombre"
                                    value={this.state.nombre}
                                    onChange={this.handleChange}
                                    placeholder="Ingrese gÃ©nero"
                                    required>
                                </input>
                                
                            </div>
                            <div className="form-group col s12">
                                <button type="submit" className="btn btn-success float-right" > Agregar GÃ©nero </button>
                                </div>
                        </div>
                    </form>
                </div>
               

        
                    <form onSubmit={this.eliminarGenero}>
                    <div className="cardEliminarAutor offset-md-1">
                        <div className="card-header">
				            <h5>Eliminar gÃ©nero</h5>
                            </div> 
                            <div className="card-body">
                            <select className="form-control" onChange={this.onInputChange2} id="exampleFormControlSelect1" name="editorial">
                            <option selected>Seleccione un gÃ©nero para eliminar</option>
                                {this.state.generos.map(ge =>
                                    <option key={ge.id} value={ge._id} >{ge.nombre}</option>
                                )}
                            </select>
                        </div>
                        <div className="form-group col s12">
                            <button type="submit" className="btn btn-success float-right" > Eliminar GÃ©nero </button>
                        
                            </div>
                            </div>
                    </form>
               
                


                
               
                    <form onSubmit={this.modificarGenero}>

                    <div className="cardEditarEditorial offset-md-1">
                        <div className="card-header">
                        <h5>Modificar GÃ©nero</h5>
                            </div>
                            <div className="card-body"> 
                            <select required className="form-control" onChange={this.onInputChange3} id="exampleFormControlSelect1" name="editorial">
                            <option selected>Seleccione un gÃ©nero para modificar</option>
                                {this.state.generos.map(ge =>
                                    <option key={ge.id} value={ge._id} >{ge.nombre}</option>
                                )}
                            </select>
                            <label className="text-light">Ingrese el nuevo gÃ©nero</label>
                            <input
                                className="form-control col s12"
                                id="nombre2"
                                name="nombre2"
                                value={this.state.nombre2}
                                onChange={this.handleChange2}
                                required
                                placeholder="Ingrese el nuevo gÃ©nero">
                            </input>
                        </div>



                        <div className="form-group col s12 ">
                            <button type="submit" className="btn btn-success float-right" >
                                Actualizar GÃ©nero
                            </button>
                        </div>
                        </div>

                    </form>
                </div>
                
                

                <div className="col-md-4">
                    {this.state.generos.map(ge =>
                        <div class="card col-md-10 offset-md-5 text-light bg-dark" >
                            <div class="card-body">
                    
                                <h5 class="card-title" onChange={this.onInputChange2} >{ge.nombre}</h5>
                    
                            </div>
                        </div>
                    )}
                </div>
            </div>
            </div>
        )
    }
}
export default Generos;
