import React, { Component } from 'react';
import axios from 'axios';
import ItemPerfil from './ItemPerfil';
import { Link } from 'react-router-dom';

//Poner constante a la cual le hacemos la consulta de nuestros pefiles como suscriptor
const perfiles = 'http://localhost:4000/api/perfiles'

class ListarPerfiles extends Component{
    constructor(){
        super();
        this.state={
            user: JSON.parse(sessionStorage.getItem('user')),
            token: sessionStorage.getItem('token'),
            perfiles: []

        }
    }

    //Seteamos los perfiles en el arrgelo
    setPerfiles (res) {
        this.setState({perfiles:res});
    }

    //Hacemos la consulta de los perfiles del suscriptor
    getPerfiles = async () => {
        await axios.post( perfiles,
            { id: this.props.id },
            { headers: {'xaccess': sessionStorage.getItem('token')}}

        ) 
        .then (res => {
            console.log(res.data);
            this.setPerfiles(res.data)
        })
        .catch();
    }

    async componentDidMount(){
        this.getPerfiles();
    }

    render (){
        return(
            <div className="container">
                {this.state.perfiles.map(per =>
                    <div>
                      {per.map( perfil =>
                        <div>
                            <ItemPerfil perfil={perfil}></ItemPerfil>
                        </div>)}  
                    </div>
                    )}

            </div>
        )
    }

}



export default ListarPerfiles;
