import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import 'react-confirm-alert/src/react-confirm-alert.css';
import StarRatings from 'react-star-ratings';



class Reseña extends Component {
    constructor(props) {
        super(props)
        this.state = {
            //idLibro: this.props.match.params,
            calificacion: null,
            titulo: '',
            cuerpo: '',
            ranting: '',
        }

        this.changeRating = this.changeRating.bind(this);

    }

    changeRating(newRating, name) {
        this.setState({
            rating: newRating
        });
    }

    componentDidMount = () => {


    }

    onSubmit = () => {
        alert("Boton Funcando");
    }
    onInputChange = () => {

    }


    render() {


        return (
            <div className="container-reseña">
                <div className="cardReseña col-md-6 offset-md-3 text-light bg-dark ">
                    <h4 className="card-header">Reseña para el libro: </h4>

                    <span><h5 className="card-header">Calificacón </h5></span>
                    <StarRatings
                        rating={this.state.rating}
                        starRatedColor="yellow"
                        changeRating={this.changeRating}
                        numberOfStars={5}
                        name='rating'
                        rating={2.403}
                        starDimension="30px"
                        starSpacing="15px"
                        starHoverColor="yellow"
                    />


                    <br></br>
                    <br></br>



                    <form onSubmit={this.onSubmit} >

                        <div className="form-group">
                            <input
                                className="form-control"
                                id="titulo"
                                name="titulo"
                                onChange={this.onInputChange}
                                value={this.state.titulo}
                                placeholder="Título"
                                required>
                            </input>
                        </div>

                        <div className="form-group">
                            <textarea className="form-control"
                                id="cuerpo"
                                rows="3"
                                name="descripcion"
                                onChange={this.onInputChange}
                                value={this.state.cuerpo}
                                placeholder="Descripción"
                                required >
                            </textarea>
                        </div>

                        <div className="form-group">
                            <button type="submit" className="btn  btn-outline-danger itemBoton float-right">
                                Agregar reseña
                            </button>
                        </div>


                    </form>






                </div>
            </div>

        )
    }
}

export default Reseña;
