import React, { Component } from 'react';
import 'react-confirm-alert/src/react-confirm-alert.css';
import axios from "axios";
import { Link } from 'react-router-dom';
import Perfiles from './ListadoPerfiles';

const misPerfiles = "http://localhost:4000/api/suscriptores/loginPerfiles";
const cargar = "http://localhost:4000/api/suscriptores/agregarPerfil";

class AgregarPerfil extends Component {
    constructor(props) {
        super(props)
        this.state = {
            token: sessionStorage.getItem("token"),
            suscripcion: JSON.parse(sessionStorage.getItem("user")),
            miPerfil:JSON.parse(sessionStorage.getItem("perfilUser")),
            nombre:'',
            misPerfiles: []
        }

        this.onInputChange = this.onInputChange.bind(this);



    }


    getDatos = async () => {
            await axios.get(misPerfiles, 

                {
                headers: { 'xaccess': sessionStorage.getItem('tokenSuscriptor') }
            })
                .then(res => {
                    this.setState({
                        misPerfiles:res.data
                    })
                })
                .catch(err => {
                    alert(err.response)
                });



    };



    componentDidMount = async () => {
        this.getDatos();
        console.log("Mi Suscripción es: ", this.state.suscripcion)
        console.log("Mi Perfil es: ", this.state.miPerfil)

    }

    onSubmit = async (e) => {
        e.preventDefault();
     if (this.state.suscripcion.user.suscripcion === "REGULAR" && this.state.misPerfiles.length < 2 || this.state.suscripcion.user.suscripcion === "PREMIUM" && this.state.misPerfiles.length < 4) { 
        await axios .post(cargar,
        { id: this.state.suscripcion.user._id,
        nombre:this.state.nombre },
        { headers: { xaccess: this.state.token } }
      )
      .then((res) => {
        alert(JSON.stringify(res.response.data.msg));
        window.location.reload(true);

        })

      .catch((err) => {
        console.log(err);
        window.location.reload(true);

      });
    } else {alert("No podes agregar otro perfil, ya estas en tu maximo permitido, borrá alguno para agregar otro") 

    }
  };


    onInputChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })

    };


    render() {

        return (

                <div className="cardAgregarPerfil col-md-6 offset-md-3 text-light bg-dark ">
                    <h4 className="card-header">Perfiles de {this.state.suscripcion.user.nombre} </h4>

                    <span><h5 className="card-header">Sus perfiles: </h5></span>

                        <Perfiles/>

                    <form onSubmit={this.onSubmit} >

                        <div className="form-group">
                            <input className="form-control"
                                id="nombre"
                                name="nombre"
                                onChange={this.onInputChange}
                                placeholder="Nombre del nuevo perfil "
                            >
                            </input>
                        </div>



                            <button type="submit" className="btn  btn-outline-danger itemBoton float-right">
                                Agregar perfil
                            </button>


                    </form>






                </div>


        )
    }
}

export default AgregarPerfil;
