import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from '../../../../node_modules/axios';
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
import StarRatings from 'react-star-ratings';
import { Alert } from 'react-bootstrap';


const eliminar= 'http://localhost:4000/api/resenas/eliminar';
const spoiler= 'http://localhost:4000/api/resenas/spoiler';

class ItemReseñaAdmin extends Component {
    constructor(props) {
        super(props)
        this.state = {
            token: sessionStorage.getItem('token'),
            reseña:props.reseña,
            ranting:props.reseña.puntaje,
            checked:props.reseña.spoiler
           
        }
      

    }

   
    
    async componentDidMount(){
       
    }

    eliminarReseña= async()=>{
        console.log(this.state.reseña._id );
        await axios
        .post(
            eliminar,
            { id: this.state.reseña._id },
            { headers: { xaccess: this.state.token } }
        )
        .then((res) => {
            console.log(res.data);
         
        })
        .catch((err) => {
            console.log(err.data);
          
        });

    }

    marcarComoSpoiler= async()=>{
        console.log(this.state.reseña._id );
        await axios
        .post(
            spoiler,
            { id: this.state.reseña._id },
            { headers: { xaccess: this.state.token } }
        )
        .then((res) => {
            console.log(res.data);
            alert(JSON.stringify(res.response.data.msg));
            alert('RESEÑA RESEÑA FUE MARCADA COMO SPOILER')
         
        })
        .catch((err) => {
            console.log(err.data);
          
        });


    }
    handleChange = () => {
        this.setState({ checked: !this.state.checked })
    }

    render() {
      
       
        const show =this.props.reseña.spoiler;    
        return (
            <div className='form-novedad'>
                
                <div class="card col-md-6 offset-md-3 text-light bg-dark" >
                    <div class="card-body">

                    <h5 class="card-title "> {this.props.reseña.autor.nombre}</h5>
                        <StarRatings
                        rating={this.state.ranting}
                        starRatedColor={'yellow'}
                        
                        numberOfStars={5}
                        name='rating'
                        starDimension="30px"
                        starSpacing="15px"
                        starHoverColor="yellow"
                        starSelectingHoverColor="yellow"
                        isSelectable={true}
                        isAggregateRating={true}

                    />
                     { show?(<text>{" ¡Spoiler!"}</text> ):
                       
                       (<div> </div>)
                       
                    } 

                 
                  <br></br>
                  <h5 class="card-title "> {this.props.reseña.comentario}</h5>
               
                        <button className="btn btn-outline-danger itemBoton" onClick={() => confirmAlert({
                            customUI: ({ onClose }) => {
                                return (
                                    <div className='custom-ui'>
                                        <h1>¿Está seguro?</h1>
                                        {' '}
                                        
                                        <button onClick={onClose}>No</button> {' '}
                                        <button
                                            onClick={() => {
                                                this.eliminarReseña();
                                                onClose();


                                            }}
                                        >
                                            Si, deseo eliminar
                             </button>
                                    </div>
                                );
                            }
                        })}>Eliminar</button> {''}
                        
                       { show?( <h5></h5>):
                       
                       (<button className="btn btn-outline-danger itemBoton" onClick={this.marcarComoSpoiler}> Marcar como spolier </button>)
                       
                    } 
                       
                        
                       </div>
                    </div>
                </div>
           
        )
    }
}

export default ItemReseñaAdmin;
