import React, { Component } from 'react';

import axios from 'axios';


import { Link } from 'react-router-dom';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import ItemReseñaAdmin from './ItemReseñaAdmin';

const libro="http://localhost:4000/api/libros/me"
const reseñas= 'http://localhost:4000/api/libros/reseñas';
class ReseñasAdmin extends Component {
    constructor(props){
        super(props);
        this.state={
            libroId:  this.props.match.params.id,
            libro:'',
            user: JSON.parse(sessionStorage.getItem('user')),
            token: sessionStorage.getItem('token'),
            reseñas:[{
                titulo:'muy buen libro',
            },{
                titulo:'lo recomiendo',
            },{
                titulo:'malisimooo',
            },
            {
                titulo:'al final muere Thanos',
            }

        
        ],
            reseñasCarrousel:[],
         
            
        }

    }
    setReseñas=()=>{

    }
   
    getLibro=async()=>{
        await axios
        .post(
          libro,
          { id: this.state.libroId },
          { headers: { xaccess: this.state.token } }
        )
        .then((res) => {
         this.setState({
            libro: res.data,
          });
         
        })
        .catch((err) => {
          
        });
  
    }

    getData = async () =>{
        const {user} = this.state.user;
        
      
        await axios.get(reseñas,{
            libro: this.state.libroId,
            headers:{'xaccess':this.state.token}  
        })
        .then(res =>{
            this.setReseñas(res.data)
        })
        .catch();



    }
    async componentDidMount(){
        this.setReseñasCarrusel(this.state.reseñas);
        this.getLibro();
       
    }
    setReseñasCarrusel(res){
        var num =2;
        var aux=[]; 
        var aux2=[];

        res.map(re=>{
            if(aux.length< num){
                aux.push(re);
            }else{
                aux.push(re);
                aux2.push(aux);
                aux=[] 
            };
        })
        if(aux!=[]){
            aux2.push(aux);
        }
        this.setState({
            
            reseñasCarrousel:aux2,
        });
    }
   
  

    render() {
        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            className: 'slides',
            
          };
        
            return (
                    <div>

               
                   
                    <div className="carrusel">

            <h1> {this.state.libro.titulo}</h1>

                        <Slider {...settings} >

                            {this.state.reseñasCarrousel.map(re => 
                            <div>
                                {re.map( reseña=>
                                    <div>
                                        <ItemReseñaAdmin reseña={reseña}></ItemReseñaAdmin>    
                                    </div>  
                                )}
                            </div>
                            )} 


                        </Slider>              

                    </div>
                
                </div>
            )
        
        
    }
}
export default ReseñasAdmin;
