import React, { Component } from 'react';
import axios from 'axios';
import ItemPerfil from './ItemPerfil';
import { Link } from 'react-router-dom';



//Poner constante a la cual le hacemos la consulta de nuestros pefiles como suscriptor
const perfilesApi = 'http://localhost:4000/api/suscriptores/loginPerfiles'
const miperfilApi = 'http://localhost:4000/api/perfiles/me'
const loginPerfilApi = 'http://localhost:4000/api/suscriptores/loginPerfil'

class ListarPerfiles extends Component{
    constructor(){
        super();
        this.state={
            token: sessionStorage.getItem('token'),
            perfiles: [],
            perfilesNombre: [],

        }
        this.nombrePerfil = this.nombrePerfil.bind(this);
        this.verNombre = this.verNombre.bind(this);
    }



   nombrePerfil = async (idDelPerfil)=> {

        await axios.post( miperfilApi,
            {id: idDelPerfil},
            {headers: {'xaccess':sessionStorage.getItem('token')}  
        }).then(res =>{
            this.state.perfilesNombre.push(res.data.nombre) });
        this.verNombre(0);
       console.log(this.state.perfiles, this.state.perfilesNombre)
    }

    async irAHomeDePerfil(e){

        const idPerfil = this.state.perfiles[e.value];
        await axios.post (loginPerfilApi,
                {id:idPerfil},
                {headers: {'xaccess':sessionStorage.getItem('token')} 
            })
            .then(res => {
                console.log(res)
                //REDIRECT A HOME

            });



    }


    verNombre = (index) => {
        console.log(this.state.perfilesNombre[index]);

    }

    async componentDidUpdate() {

        this.state.perfiles.map((perfil, index)=>{
            this.nombrePerfil(perfil)
        })
    }

    async componentDidMount(){

        await axios.get( perfilesApi,
            {headers: {'xaccess': sessionStorage.getItem('token')}}
       ) 
       .then (res => {
           this.setState({perfiles:res.data});
       }) 

    }



    render(){
        return(
            <div className="container">
            <div className="card col-md-6 offset-md-3 text-light bg-dark display-flex center" >

                <li>
                    { 
                        this.state.perfilesNombre.map((nombre , index)=>{
                            this.verNombre(index);

                        })
                    }

               </li>  

            </div>
            </div>



        )
    }

}



export default ListarPerfiles;
