import React, { Component } from 'react';

import axios from 'axios';


import { Link } from 'react-router-dom';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import ItemListTrailer from './ItemListTralerSuscriptor';


const trailersApi= 'http://localhost:4000/api/trailers/';

export default class ListarTrailerAdmin extends Component {
    constructor(){
        super();
        this.state={
            user: JSON.parse(sessionStorage.getItem('user')),
            token: sessionStorage.getItem('token'),
            trailers:[],
            trailersCarrousel:[],
            
        }

    }
    setLibros(res){
        var num =2;
        var aux=[]; 
        var aux2=[];

        res.map(trailer=>{
            if(aux.length< num){
                aux.push(trailer);
            }else{
                aux.push(trailer);
                aux2.push(aux);
                aux=[] 
            };
        })
        if(aux!=[]){
            aux2.push(aux);
        }
        this.setState({
            trailers:res,
            trailersCarrousel:aux2,
        });
    }

    getData = async () =>{
        const {user} = this.state.user;
        
      
        await axios.get(trailersApi,{
            user: user,
            headers:{'xaccess':this.state.token}  
        })
        .then(res =>{
            this.setLibros(res.data)
        })
        .catch();



    }
    async componentDidMount(){

        this.getData();
    }
   
  

    render() {
        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            className: 'slides',
            
          };
        
            return (
                    <div>

                    <div className="carrusel">
                        <Slider {...settings} >

                             {this.state.trailersCarrousel.map(trai=> 
                            <div>
                               {trai.map(trailer=>
                                <div>
                                    <ItemListTrailer trailer={trailer}></ItemListTrailer>    
                                </div>  
                                )}
                            </div>
                            )} 

                               
                         
                        </Slider>
                    </div>
                
                </div>
            )
        
        
    }
}
