import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';



const miperfilApi = 'http://localhost:4000/api/perfiles/me';

class ListadoPerfiles extends Component {
    constructor() {
        super();
        this.state= {
            token: sessionStorage.getItem("token"),
            suscripcion: JSON.parse(sessionStorage.getItem("user")),
            Perfil:JSON.parse(sessionStorage.getItem("perfilUser")),
            miPerfil:[]
        }
    }




    getData = async () => {
        await axios.get(miperfilApi,{
            headers:{'xaccess':sessionStorage.getItem('token')}  
        })
        .then(res =>{
            this.setState({
                miPerfil:res
            });
        })
        .catch(err =>{
            alert(err.response)});

    }


    componentDidMount = async () => {
        this.getData();
        console.log("Ac치 presta atenci칩n", this.state.suscripcion)
    }




    render() {
        return (
            <div>
               {this.state.suscripcion.perfiles.map ( perfil =>
               <React.Fragment>
                <div class="card-body" > perfil.nombre </div>
                <div class="card col-md-6 offset-md-3 text-light bg-dark" >
                    <div class="card-body">
                        <h5 className="card-title ">  </h5>
                        <Link className='btn btn-outline-danger itemBoton float-right' >
                            Ver detalle
                        </Link>
                    </div>
                </div>
                </React.Fragment>
               )}
            </div>
        )

    }
}

export default ListadoPerfiles;
