import React, { Component } from 'react';

import axios from 'axios';

import ItemListLibroAdmin from './ItemListLibroAdmin';
import { Link } from 'react-router-dom';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import ItemReseñaAdmin from './ItemReseñaAdmin';


const reseñas= 'http://localhost:4000/api/libros/reseñas';
export default class ReseñasAdmin extends Component {
    constructor(){
        super();
        this.state={
            libro:122,
            user: JSON.parse(sessionStorage.getItem('user')),
            token: sessionStorage.getItem('token'),
            reseñas:[{
                titulo:'reseña uno',
            },{
                titulo:'reseña dos'
            }],
         
            
        }

    }
    setReseñas=()=>{

    }
   

    getData = async () =>{
        const {user} = this.state.user;
        
      
        await axios.get(reseñas,{
            libro: this.state.id,
            headers:{'xaccess':this.state.token}  
        })
        .then(res =>{
            this.setReseñas(res.data)
        })
        .catch();



    }
    async componentDidMount(){

       
    }
   
  

    render() {
        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            className: 'slides',
            
          };
        
            return (
                    <div>

               
                    <div >
                        <Link to='/libro/nuevo' className='btn btn-success col-md-6 offset-md-3'>Cargar Metadata de un Libro</Link>
                    </div>
                    <div className="carrusel">
                        <Slider {...settings} >

                             {this.state.reseñas.map(re => 
                            <div>

                                <ItemReseñaAdmin reseña={re}></ItemReseñaAdmin>    

                            </div>
                            )} 

                               
                         
                        </Slider>
                    </div>
                
                </div>
            )
        
        
    }
}
