import React, { Component } from 'react';

import axios from 'axios';


import { Link } from 'react-router-dom';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import ItemReseñaAdmin from './ItemReseñaAdmin';

const libro="http://localhost:4000/api/libros/me"
const reseñas= 'http://localhost:4000/api/resenas/';
class ReseñasAdmin extends Component {
    constructor(props){
        super(props);
        this.state={
            libroId:  this.props.match.params.id,
            libro:'',
            user: JSON.parse(sessionStorage.getItem('user')),
            token: sessionStorage.getItem('token'),
            reseñas:[{
                comentario:'muy buen libro',
                ranting:5,
                spoiler:false,
            },{
                comentario:'lo recomiendo',
                ranting:4,
                spoiler:true,
            },{
                comentario:'malisimooo',
                ranting:1,
                spoiler:false,
            },
            {
                comentario:'al final muere Thanos',
                ranting:3,
                spoiler:true,
            }

        
        ],
        reseñasCarrousel:[],
        re:[],
        promedio:0,
         
            
        }

    }
    setReseñas=()=>{

    }
   
    getLibro=async()=>{
        await axios
        .post(
          libro,
          { id: this.state.libroId },
          { headers: { xaccess: this.state.token } }
        )
        .then((res) => {
         this.setState({
            libro: res.data,
          });
         
        })
        .catch((err) => {
          
        });
  
    }

    getData = async () =>{
        const {user} = this.state.user;
        console.log('esta trayendo reseñas');
        await axios
         .post(reseñas,
            { id: this.state.libroId },
            { headers: { xaccess: this.state.token } }
        )
        .then(res =>{
            console.log(res.data);
            this.setState({
                re: res.data,
              });
        })
        .catch();



    }
    async componentDidMount(){
        this.setReseñasCarrusel(this.state.reseñas);
        this.getLibro();
        this.getData();
       
    }
    setReseñasCarrusel(res){
        var num =2;
        var aux=[]; 
        var aux2=[];

        res.map(re=>{
            if(aux.length< num){
                aux.push(re);
            }else{
                aux.push(re);
                aux2.push(aux);
                aux=[] 
            };
        })
        if(aux!=[]){
            aux2.push(aux);
        }
        this.setState({
            
            reseñasCarrousel:aux2,
        });
    }
   
  

    render() {
        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            className: 'slides',
            
          };
          
        
            return (
                    <div>

               
                   
                    <div className="carrusel">

          
                    <div class="card col-md-6 offset-md-3 text-light bg-dark" >
                    <div class="card-body">
                    <h4 className="card-title ">Reseñas del libro {this.state.libro.titulo} </h4>
                    <h4 className="card-title ">raiting del libro {this.state.ranting} </h4>
                    </div>

                    </div>

                        <Slider {...settings} >

                            {this.state.reseñasCarrousel.map(re => 
                            <div>
                                {re.map( reseña=>{
                                    this.setState({
                                        promedio: reseña.ranting
                                    });
                                    <div>
                                        <ItemReseñaAdmin reseña={reseña}></ItemReseñaAdmin>    
                                    </div> 
                                    
                                }
                                )}
                            </div>
                            )} 


                        </Slider>              

                    </div>
                
                </div>
            )
        
        
    }
}
export default ReseñasAdmin;
