import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import 'react-confirm-alert/src/react-confirm-alert.css';
import ReactStars from 'react-stars';
import { render } from 'react-dom';



class Reseña extends Component {
    constructor(props) {
        super(props)
        this.state = {
            //idLibro: this.props.match.params,
            calificacion: null,
            titulo: '',
            cuerpo: '',
        }

    }

    componentDidMount = () => {


    }

    onSubmit = () => {
        alert("Boton Funcando");
    }
    onInputChange = () => {

    }


    render() {
        const ratingChanged = (newRating) => {
            console.log(newRating)
          }

        return (
            <div className="container-reseña">
                <div className="cardReseña col-md-6 offset-md-3 text-light bg-dark ">
                    <h4 className="card-header">Reseña para el libro: </h4>
                    <br></br>
                    <ReactStars
                        count={5}
                        onChange={ratingChanged}
                        size={24}
                        color2={'#ffd700'} />,


                    <form onSubmit={this.onSubmit} >

                        <div className="form-group">
                            <input
                                className="form-control"
                                id="titulo"
                                name="titulo"
                                onChange={this.onInputChange}
                                value={this.state.titulo}
                                placeholder="Título"
                                required>
                            </input>
                        </div>

                        <div className="form-group">
                            <textarea className="form-control"
                                id="cuerpo"
                                rows="3"
                                name="descripcion"
                                onChange={this.onInputChange}
                                value={this.state.cuerpo}
                                placeholder="Descripción"
                                required >
                            </textarea>
                        </div>

                        <div className="form-group">
                            <button type="submit" className="btn  btn-outline-danger itemBoton float-right">
                                Agregar reseña
                            </button>
                        </div>


                    </form>






                </div>
            </div>

        )
    }
}

export default Reseña;
