import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';


//Constante a la cual hacemos la consulta
const librosApi= 'http://localhost:4000/api/perfiles/visitadoLibro';
const capituslosApi= 'http://localhost:4000/api/perfiles/visitadoCapitulo';


const miperfilApi = 'http://localhost:4000/api/perfiles/me';


export default class MiSuscripcion extends Component {
    constructor(){
        super();
        this.state={
            token: sessionStorage.getItem('token'),
            miPerfil:[],
            historialLibros:[],
            historialCapitulos:[],

        }

    }



    setPerfiles(res){
        this.setState({
            miPerfil:res
        });
        console.log(this.state.miPerfil);
    }

    getDataPerfil = async () =>{
        await axios.get(miperfilApi,{
            headers:{'xaccess':sessionStorage.getItem('token')}  
        })
        .then(res =>{
            this.setPerfiles(res.data);
            this.getHistorialCapitulos();
            this.getHistorialLibros();
        })
        .catch(err =>{
            alert(err.response)});

    }

    getHistorialCapitulos = async () =>{
        await axios.get(miperfilApi,{
            headers:{'xaccess':sessionStorage.getItem('token')}  
        })
        .then(res =>{
            this.setPerfiles(res.data);
            this.getHistorialCapitulos();
            this.getHistorialLibros();
        })
        .catch(err =>{
            alert(err.response)});

    }


    async componentDidMount(){

        this.getDataPerfil();



    }

    render() {
        return (
            <div className="container">
            <div class="cardVS col-md-6 offset-md-3 text-light bg-dark" >
                <h1 class="card-header"> Suscriptor:  {this.state.miSuscripcion.nombre} </h1>
                <h2 class="card-header"> Historial del perfil:  {this.state.miPerfil.nombre} </h2>
                    <div class="card-body"> 

                        <div>
                        <Link to={'/suscriptor/suscripcion/modificar'} className='btn login_btn float-right'> Modificar</Link>
                        <Link to={'/suscriptor/suscripcion/pefil/historial'} className='btn login_btn float-left'> Historial</Link>
                        </div>
                    </div>


            </div>


        </div>


        )
    }
}
