import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import Libros from "../../VistaAdmin/libros/Libros";

//Constante a la cual hacemos la consulta
const librosApi = "http://localhost:4000/api/libros/me";
const capitulosApi = "http://localhost:4000/api/libros/verCapitulo";
const capitulosMuchosApi = "http://localhost:4000/api/libros/capitulosMuchos";
const existeL = "http://localhost:4000/api/libros/existeLibro";
const existeC = "http://localhost:4000/api/libros/existeCapitulo";
const miperfilApi = "http://localhost:4000/api/perfiles/me";

export default class MiSuscripcion extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: sessionStorage.getItem("token"),
      miPerfil: [],
      historialLibros: [],
      historialCapitulos: [],

      librosConInfoTermiado: [],
      capitulosConInfoTerminado: [],
    };
    this.filtrar = this.filtrar.bind(this);
    this.getLibros = this.getLibros.bind(this);
    this.getCapitulos = this.getCapitulos.bind(this);
  }
  getLibros = async () => {
    var librosAux = new Array();
    this.state.historialLibros.map(async (libro) => {
      await axios
        .post(
          librosApi,
          { id: libro._id },
          { headers: { xaccess: sessionStorage.getItem("token") } }
        )
        .then((res) => {
          librosAux.push(res.data);
        });
    });
    this.setState({
      historialLibros: librosAux,
    });
  };
  async getCapitulos() {
    console.log("vino a buscar los capitulos:", this.state.historialCapitulos);

    this.state.historialCapitulos.map((capitulo) => {
      console.log("adentro del map");
      console.log("capitulo objeto:", capitulo);
      console.log("capitulo. capitulo:", capitulo.capitulo);
      /*  await axios
        .post(
          capitulosApi,
          { id: capitulo.capitulo },
          { headers: { xaccess: sessionStorage.getItem("token") } }
        )
        .then((res) => {
          capitulosAux.push(res.data);
        });
    */
    });

    /*
    this.setState({
      historialCapitulos: capitulosAux,
    });*/
  }

  filtrar = async () => {
    //filtro los existentes

    if (this.state.historialLibros.length > 0) {
      var existentes = new Array();
      this.state.historialLibros.map(async (libro) => {
        await axios
          .post(
            existeL,
            {
              id: libro._id,
            },
            { headers: { xaccess: sessionStorage.getItem("token") } }
          )
          .then((res) => {
            if (res.data.existe) {
              existentes.push(libro);
            }
          });
      });
      this.setState({
        historialLibros: existentes,
        librosConInfoTermiado: existentes,
      });
      if (this.state.historialLibros.length) {
        this.getLibros();
      }
    }

    if (this.state.historialCapitulos.length > 0) {
      var existentes = new Array();
      this.state.historialCapitulos.map(async (capitulo) => {
        await axios
          .post(
            existeC,
            {
              id: capitulo._id,
            },
            { headers: { xaccess: sessionStorage.getItem("token") } }
          )
          .then((res) => {
            if (res.data.existe) {
              existentes.push(capitulo);
            }
          });
      });

      this.setState({
        historialCapitulos: existentes,
        capitulosConInfoTerminado: existentes,
      });

      if (this.state.historialCapitulos) {
        this.state.historialCapitulos.map((hola) => console.log("hola"));
        console.log("va a buscar los capitulos");
        this.getCapitulos();
      }
    }
  };
  async componentDidMount() {
    var historialL = new Array();
    var historialC = new Array();

    await axios
      .get(miperfilApi, {
        headers: { xaccess: sessionStorage.getItem("token") },
      })
      .then((res) => {
        res.data.historialLibros.map((libro) => {
          historialL.push(libro);
        });
        res.data.historialCapitulos.map((cap) => {
          historialC.push(cap);
        });
        this.setState({
          historialLibros: historialL,
          historialCapitulos: historialC,
          miPerfil: res.data,
        });
        this.filtrar();
      })
      .catch((err) => {
        alert(err.response);
      });
  }

  render() {
    return (
      <div className="container">
        <div class="cardVS col-md-6 offset-md-3 text-light bg-dark">
          <div>
            <h2 class="card-header">
              {" "}
              Historial de {this.state.miPerfil.nombre}{" "}
            </h2>
          </div>
          <div class="card-body">
            {this.state.historialLibros.map((libro, index) => (
              <div key={index}> {(libro._id, console.log(libro))}</div>
            ))}
            {this.state.historialCapitulos.map((capitulo, index) => (
              <div key={index}> {(capitulo._id, console.log(capitulo))} </div>
            ))}
            <div></div>
          </div>
        </div>
      </div>
    );
  }
}
