import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import Libros from "../../VistaAdmin/libros/Libros";

//Constante a la cual hacemos la consulta
const librosApi = "http://localhost:4000/api/libros/me";
const existeL = "http://localhost:4000/api/libros/existeLibro";
const existeC = "http://localhost:4000/api/libros/existeCapitulo";
const miperfilApi = "http://localhost:4000/api/perfiles/me";

export default class MiSuscripcion extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: sessionStorage.getItem("token"),
      miPerfil: [],
      historialLibros: [],
      historialCapitulos: [],
    };
    this.filtrar = this.filtrar.bind(this);
  }

  filtrar = async () => {
    console.log(this.state.historialLibros.length == 0);
    if (this.state.historialLibros > 0) {
      var existentes = new Array();
      this.state.historialLibros.map(async (libro) => {
        await axios
          .post(
            existeL,
            {
              id: libro._id,
            },
            { headers: { xaccess: sessionStorage.getItem("token") } }
          )
          .then((res) => {
            if (res.data) {
              existentes.push(libro);
            }
          });
      });
    }
    if (this.state.historialLibros > 0) {
      var existentes = new Array();
      this.state.historialLibros.map(async (libro) => {
        await axios
          .post(
            existeL,
            {
              id: libro._id,
            },
            { headers: { xaccess: sessionStorage.getItem("token") } }
          )
          .then((res) => {
            if (res.data) {
              existentes.push(libro);
            }
          });
      });
    }
  };
  async componentDidMount() {
    var historialL = new Array();
    var historialC = new Array();

    await axios
      .get(miperfilApi, {
        headers: { xaccess: sessionStorage.getItem("token") },
      })
      .then((res) => {
        res.data.historialLibros.map((libro) => {
          historialL.push(libro);
        });
        res.data.historialCapitulos.map((cap) => {
          historialC.push(cap);
        });
        this.setState({
          historialLibros: historialL,
          historialCapitulos: historialC,
          miPerfil: res.data,
        });
        this.filtrar();
      })
      .catch((err) => {
        alert(err.response);
      });
  }

  render() {
    console.log(this.state.historialLibros);
    console.log(this.state.historialCapitulos);
    console.log(this.state.miPerfil.nombre);

    return (
      <div className="container">
        <div class="cardVS col-md-6 offset-md-3 text-light bg-dark">
          <div>
            <h2 class="card-header">
              {" "}
              Historial de {this.state.miPerfil.nombre}{" "}
            </h2>
          </div>
          <div class="card-body">
            {this.state.historialLibros.map((libro, index) => (
              <div key={index}> {libro.titulo}</div>
            ))}
            {this.state.historialCapitulos.map((capitulo, index) => (
              <div key={index}> {capitulo.titulo} </div>
            ))}
            <div></div>
          </div>
        </div>
      </div>
    );
  }
}
