import React, { Component } from 'react';
import axios from 'axios';




import { Link } from 'react-router-dom';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";


import ItemReporteLibros from './ItemReporteLibros';

const Libro="http://localhost:4000/api/reportes/libros";
class ReporteDeLibros extends Component {

    constructor(props){
        super(props);
        this.state={
            libros:[],
            token: sessionStorage.getItem("token"),
            hayReportes:true
        }

    }
    componentDidMount= async ()=>{
            await axios
            .get(Libro,{
                 headers: { xaccess: this.state.token } }
            )
            .then((res) => {
                console.log(res.data)
                this.setState({libros:res.data})

            });
    };

    
    render() {
        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            className: 'slides',
            
          };

        const show = this.state.hayReportes;

        
            return (
                    <div>

               
                    <div >
                        <Link to='/trailers/nuevo' className='btn btn-danger col-md-6 offset-md-3'>Cargar Trailer</Link>
                    </div>


                { show ?
                    <React.Fragment>
                    <div className="carrusel">
                        <Slider {...settings} >

                             {this.state.trailersCarrousel.map(trai=> 
                            <div>
                               {trai.map(trailer=>
                                <div>
                                    <ItemReporteLibros reporte={libro}></ItemReporteLibros>    
                                </div>  
                                )}
                            </div>
                            )} 

                               
                         
                        </Slider>
                    </div>
                    </React.Fragment>

                    : 
                    <React.Fragment>
                    <div className="cardErrorTrailer">
                        <h4>POR EL MOMENTO NO HAY TRAILERS</h4>

                    </div>
                    </React.Fragment>
                }
                
                </div>
            )
        
        
    }
}
export default ReporteDeLibros;
